import consumer from "./consumer"

consumer.subscriptions.create("RoomChannel", {
  connected() {
    // Called when the subscription is ready for use on the server
  },

  disconnected() {
    // Called when the subscription has been terminated by the server
  },

  received(data) {
    // Called when there's incoming data on the websocket for this channel
    var node = document.createElement("P");
    // var node1 = document.createElement("H");

    // debugger;
    var textnode = document.createTextNode(data.content.message); 
    var textnode1 = document.createTextNode('Sent At' + data.content.created_at); 

    document.getElementById("new_message").style.color = "green";
    // debugger;
    node.appendChild(textnode); 
    // eval("textnode1") + "<hr>";

    node.appendChild(textnode1); 

    document.getElementById("new_message").appendChild(node);
    document.getElementById('chat_message').value= ''
  }
});
